mapper.select:
SELECT 

FROM a_agent a

WHERE a.id = #{id}

SELECT 

FROM a_agent a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.phone = #{phone}


		AND a.email = #{email}


		AND a.weixin = #{weixin}


		AND a.mobile = #{mobile}


		AND a.create_date BETWEEN #{beginCreateDate} AND #{endCreateDate}


		AND a.login_name = #{loginName}


		AND a.state = #{state}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM a_agent a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE a_agent SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
