mapper.select:
SELECT 

FROM a_dlyb_product_log a

WHERE a.id = #{id}

SELECT
FROM a_dlyb_product_log a where a.articleno = #{articleno}
order by a.create_date desc limit 5

SELECT 

FROM a_dlyb_product_log a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.articleno = #{articleno}


		AND a.name LIKE  concat('%',#{name},'%')


		 AND date(a.create_date)  >= #{beginCreateDate}  


		   AND date(a.create_date)  <= #{endCreateDate}  


		AND a.brandname = #{brandname}


		AND a.state = #{state}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM a_dlyb_product_log a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE a_dlyb_product_log SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
