mapper.select:
SELECT 

FROM a_simple_order_after a

WHERE a.id = #{id}

SELECT 

FROM a_simple_order_after a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM a_simple_order_after a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE a_simple_order_after SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
